(function($){function requestWithTimeout(requestFn,timeout,url,data,success,dataType){var started=new Date();return requestFn(url,data,function(data,textStatus,jqXHR){if(typeof success==='function'){timeout=timeout-(new Date()-started);if(timeout){window.setTimeout(function(){success(data,textStatus,jqXHR);},timeout);}
else{success(data,textStatus,jqXHR);}}},dataType);}
$.extend({getWithTimeout:function(timeout,url,data,success,dataType){return requestWithTimeout($.get,timeout,url,data,success,dataType);},postWithTimeout:function(timeout,url,data,success,dataType){return requestWithTimeout($.post,timeout,url,data,success,dataType);},getJSONWithTimeout:function(timeout,url,data,success,dataType){return requestWithTimeout($.getJSON,timeout,url,data,success,dataType);}});})(jQuery);